import React, { useState } from 'react';

function App() {
  const [funcion, setFuncion] = useState("x**3 - 6*x + 2");
  const [a, setA] = useState(1);
  const [b, setB] = useState(2);
  const [tol, setTol] = useState(0.001);
  const [maxIter, setMaxIter] = useState(50);
  const [resultado, setResultado] = useState(null);

  const calcular = async () => {
    if (!funcion || a === undefined || b === undefined || tol === undefined || maxIter === undefined) {
      alert("Por favor, completá todos los campos.");
      return;
    }

    try {
      const res = await fetch("http://localhost:8000/biseccion", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ funcion, a, b, tol, max_iter: maxIter })
      });

      if (!res.ok) {
        const error = await res.json();
        alert("Error del servidor: " + error.detail);
        return;
      }

      const data = await res.json();
      setResultado(data);
    } catch (e) {
      alert("Error de red o del servidor.");
      console.error(e);
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1>Método de Bisección</h1>
      <label>f(x): </label>
      <input value={funcion} onChange={e => setFuncion(e.target.value)} /><br />
      <label>a: </label>
      <input type="number" value={a} onChange={e => setA(+e.target.value)} /><br />
      <label>b: </label>
      <input type="number" value={b} onChange={e => setB(+e.target.value)} /><br />
      <label>Tolerancia: </label>
      <input type="number" value={tol} onChange={e => setTol(+e.target.value)} /><br />
      <label>Iteraciones: </label>
      <input type="number" value={maxIter} onChange={e => setMaxIter(+e.target.value)} /><br />
      <button onClick={calcular}>Calcular</button>

      {resultado && resultado.raiz !== undefined && (
        <div style={{ marginTop: "1rem" }}>
          <h2>Raíz: {Number(resultado.raiz).toFixed(5)}</h2>
          <h3>Pasos:</h3>
          <pre>{JSON.stringify(resultado.pasos, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
